# ===================================================================================================
# Script        : crossValidateSIG_Ward.sh
# Usage         : ./crossValidateHybridSOM_C4_5.sh [simDirectory]
# Purpose       : Performs a cross-validated paired test on parameter data set using SIG*
#                 algorithm configured with Ward clustering
#
# Preconditions : - All required simulation-related programs are present
#                 - Data set as directory name in dataset directory must exist and be in correct form
#                   (i.e. no header, one record per line, comma delimited, last value is class label)
# ===================================================================================================

#!/bin/bash

# ===================================================================================================
# Function : validateSystem(simDirectory)
# Purpose  : Validates components required for cross validation run (based on simDirectory)
# Return   : None
# ===================================================================================================

validateSystem() {
	
	# check for correct number of parameters
	if [ -z ${1} ]; then
		echo "Usage: ./crossValidate.sh [simDirectory]"
		exit
	fi
	
	echo "Status: Validating system..."
	echo "=============================================="
	
	# check for data directory
	echo -n "Simulation directory \"${1}\"...  "
	if [ ! -d ./../../datasets/${1} ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	# check for original data file in simulation directory
	echo -n "Data file \"${1}/original/${1}.data\"...  "
	if [ ! -f ./../../datasets/${1}/original/${1}.data ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	# check for cross validation set building script
	echo -n "Cross validation set building script...  "
	if [ ! -f ./../../algorithms/BuildSets/BuildSets.class ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	# check for SOM_PAK data conversion script
	echo -n "Conversion script \"${1}/convert_som.awk\"...  "
	if [ ! -f ./../../datasets/${1}/convert_som.awk ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	# check for SOM_PAK names script
	echo -n "Names script \"${1}/names_som.sh\"...  "
	if [ ! -f ./../../datasets/${1}/names_som.sh ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	# check for SOM_PAK initialisation program
	echo -n "SOM initialisation program...  "
	if [ ! -f ./../../algorithms/SOM_PAK/lininit ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	# check for SOM_PAK training program
	echo -n "SOM training program...  "
	if [ ! -f ./../../algorithms/SOM_PAK/vsom ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	# check for SIG* algorithm with Ward cluster labeling program
	echo -n "SIG* algorithm with Ward cluster labeling program...  "
	if [ ! -f ./../../algorithms/SIG_Ward/SIG ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	#check for results averaging program
	echo -n "Results averaging program...  "
	if [ ! -f ./../../algorithms/AverageRunResults/AverageSIGResults.class ]; then
		echo "NOT FOUND"
		exit
	fi
	echo "FOUND"
	
	echo "=============================================="
	echo "Status: System OK..."
	
}

# ===================================================================================================
# Function : buildSets(simDirectory)
# Purpose  : Builds data sets required for cross validation simulations (based on simDirectory)
# Return   : None
# ===================================================================================================

buildSets() {
	
	echo "Status: Performing cross validation set builds"
	
	# build raw training and test sets (must still be converted to SOM_PAK and C4.5 formats)
	if [ ${1} == "ionosphere" ]; then
		echo "Status: Building set for IONOSPHERE database"
		echo "=============================================="
		java BuildSets ../../datasets/${1}/original/${1}.data , 30 -normalise
	elif [ ${1} == "iris" ]; then
		echo "Status: Building set for IRIS database"
		echo "=============================================="
		java BuildSets ../../datasets/${1}/original/${1}.data , 30 -normalise
	elif [ ${1} == "monks1" ]; then
		echo "Status: Building set for MONKS1 database"
		echo "=============================================="
		java BuildSets ../../datasets/${1}/original/${1}.data , 30
	elif [ ${1} == "monks2" ]; then
		echo "Status: Building set for MONKS2 database"
		echo "=============================================="
		java BuildSets ../../datasets/${1}/original/${1}.data , 30
	elif [ ${1} == "monks3" ]; then
		echo "Status: Building set for MONKS3 database"
		echo "=============================================="
		java BuildSets ../../datasets/${1}/original/${1}.data , 30 5
	elif [ ${1} == "pima" ]; then
		echo "Status: Building set for PIMA database"
		echo "=============================================="
		java BuildSets ../../datasets/${1}/original/${1}.data , 30 -normalise
	else
		echo "Status: Unrecognised set"
		echo "Status: Building default"
		echo "=============================================="
		java BuildSets ../../datasets/${1}/original/${1}.data , 30
	fi
	
	echo "=============================================="
	echo "Status: Cross validation sets built"
	
	echo -n "Status: Performing data set conversions...  "
	
	# create SOM names file
	./../../datasets/${1}/names_som.sh > ${1}.som.names
	
	# perform conversion of data set from raw form to SOM_PAK and C4.5 format, creating [simulation].data
	for ((dataset = 0; dataset < 30; ++dataset))
	do
		gawk -f ./../../datasets/${1}/convert_som.awk ${1}.data.raw.${dataset}.train > ${1}.data.${dataset}.train
		rm -f ${1}.data.raw.${dataset}.train
		gawk -f ./../../datasets/${1}/convert_som.awk ${1}.data.raw.${dataset}.test > ${1}.data.${dataset}.test
		rm -f ${1}.data.raw.${dataset}.test
	done
	
	echo "DONE"
	
}

# ===================================================================================================
# Function : runCrossValidation(simDirectory, neuronLabelingMethod, mapDim, learningRate, learningRateDecay, kernelWidth,
#            radiusDecay, charThreshold, charLow, charHigh, diffThreshold, diffLow, diffHigh)
# Purpose  : Performs a single cross validation using provided parameters
# Return   : None
# ===================================================================================================

runCrossValidation () {
	
	# get description string for neuron labeling method (0 = K-means cluster; 1 = Ward cluster)
	if [ ${2} -eq 0 ]; then
		echo -n "k-means labeling requested in runCrossValidation"
		echo -n "Exiting..."
		exit
	elif [ ${2} -eq 1 ]; then
		neuronLabelingMethodDescription="ward"
	fi
	
	if [ -f sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.accuracy ]; then
		rm -f sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.accuracy
	fi
	
	if [ -f sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.error ]; then
		rm -f sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.error
	fi
	
	for ((simNum = 0; simNum < 30; ++simNum))
	do
		echo -n "${simNum} "
		
		# execute SOM_PAK
		sleep 1
		./../../algorithms/SOM_PAK/vsom -din ${1}.data.${simNum}.train -vin ${1}.data.${simNum}.train -cin ${1}.${3}.${simNum}.map -cout sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}_${simNum}.map.out -lrate ${4} -lratedecay ${5} -radius ${6} -radiusdecay ${7} -rand 0 >> /dev/null 2> /dev/null
		
		# execute appropriate SIG* algorithm on SOM_PAK output
		sleep 1
		if [ ${2} -eq 0 ]; then
			echo -n "k-means labeling requested in runCrossValidation"
			echo -n "Exiting..."
			exit
		elif [ ${2} -eq 1 ]; then
			#./../../algorithms/SIG_Ward/SIG ${8} ${9} ${10} ${11} ${12} ${13} Ward continuous sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}_${simNum}.map.out ${1}.data.${simNum}.train ${1}.data.${simNum}.test ${1}.som.names sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.accuracy sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.error > /dev/null
			./../../algorithms/SIG_Ward/SIG ${8} ${9} ${10} ${11} ${12} ${13} Ward continuous sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}_${simNum}.map.out ${1}.data.${simNum}.train ${1}.data.${simNum}.test ${1}.som.names sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.accuracy sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.error >> prog_output.txt
		fi
		
		# clean up
		rm -f *.map.out
	done
	
	# calculate confidence intervals
	java AverageSIGResults -accuracy sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.accuracy sig_${1}_${neuronLabelingMethodDescription}.accuracy.summary
	java AverageSIGResults -error sig_${1}_${neuronLabelingMethodDescription}_dim_${3}_lr_${4}_lrd_${5}_kw_${6}_kwd_${7}_charThr_${8}_charL_${9}_charH_${10}_diffThr_${11}_diffL_${12}_diffH_${13}.error sig_${1}_${neuronLabelingMethodDescription}.error.summary

	echo ""
	
}

